import React, { useState } from "react";
import Papa from "papaparse";
import {
  LineChart, Line, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from "recharts";
import html2canvas from "html2canvas";

function App() {
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [darkMode, setDarkMode] = useState(false);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        setData(results.data);
        setHeaders(Object.keys(results.data[0]));
      },
    });
  };

  const exportChart = (id) => {
    const chart = document.getElementById(id);
    html2canvas(chart).then((canvas) => {
      const link = document.createElement("a");
      link.download = `${id}.png`;
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  const customTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white dark:bg-gray-800 text-sm p-3 border rounded shadow-lg">
          <p className="font-semibold text-blue-600 dark:text-blue-400">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-gray-800 dark:text-gray-200">
              {entry.name}: {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div className={`${darkMode ? "dark" : ""}`}>
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors px-4 py-10 flex flex-col items-center">
        <div className="w-full max-w-4xl bg-white dark:bg-gray-800 shadow-lg rounded-xl p-6">
          <div className="flex items-center justify-between mb-6">
            <h1 className="text-4xl font-bold text-blue-700 dark:text-blue-400">CSV Plotter</h1>
            <label className="flex items-center gap-2">
              <span className="text-sm">Dark Mode</span>
              <input
                type="checkbox"
                checked={darkMode}
                onChange={() => setDarkMode(!darkMode)}
                className="toggle-checkbox w-5 h-5"
              />
            </label>
          </div>

          <input
            type="file"
            accept=".csv"
            onChange={handleFileUpload}
            className="mb-6 w-full px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
          />

          {data.length > 0 && headers.length >= 3 && (
            <>
              {/* Line Chart with Multiple Y-Axes */}
              <div id="lineChart" className="mb-8 bg-white dark:bg-gray-800 p-6 shadow-md rounded-lg border border-gray-200 dark:border-gray-700">
                <h2 className="text-2xl font-semibold text-blue-600 dark:text-blue-300 mb-4">Line Chart (Multiple Y-Axes)</h2>
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                    <XAxis dataKey={headers[0]} stroke={darkMode ? "#ccc" : "#000"} />
                    <YAxis yAxisId="left" stroke="#3b82f6" />
                    <YAxis yAxisId="right" orientation="right" stroke="#f59e0b" />
                    <Tooltip content={customTooltip} />
                    <Legend />
                    <Line yAxisId="left" type="monotone" dataKey={headers[1]} stroke="#3b82f6" />
                    <Line yAxisId="right" type="monotone" dataKey={headers[2]} stroke="#f59e0b" />
                  </LineChart>
                </ResponsiveContainer>
                <button
                  onClick={() => exportChart("lineChart")}
                  className="mt-4 px-5 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg shadow transition"
                >
                  Download Line Chart
                </button>
              </div>

              {/* Bar Chart */}
              <div id="barChart" className="mb-6 bg-white dark:bg-gray-800 p-6 shadow-md rounded-lg border border-gray-200 dark:border-gray-700">
                <h2 className="text-2xl font-semibold text-green-600 dark:text-green-300 mb-4">Bar Chart</h2>
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={data}>
                    <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                    <XAxis dataKey={headers[0]} stroke={darkMode ? "#ccc" : "#000"} />
                    <YAxis stroke={darkMode ? "#ccc" : "#000"} />
                    <Tooltip content={customTooltip} />
                    <Legend />
                    <Bar dataKey={headers[1]} fill="#10b981" />
                  </BarChart>
                </ResponsiveContainer>
                <button
                  onClick={() => exportChart("barChart")}
                  className="mt-4 px-5 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg shadow transition"
                >
                  Download Bar Chart
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
