import React, { useState } from "react";
import Papa from "papaparse";
import {
  LineChart, Line,
  BarChart, Bar,
  AreaChart, Area,
  ScatterChart, Scatter,
  PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  YAxis as MultiYAxis
} from "recharts";
import html2canvas from "html2canvas";

const chartTypes = ["Line", "Bar", "Area", "Scatter", "Pie"];
const colors = ["#8884d8", "#82ca9d", "#ffc658", "#ff8042", "#8dd1e1"];

function App() {
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [chartType, setChartType] = useState("Line");
  const [yKeys, setYKeys] = useState([]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        setData(results.data);
        const keys = Object.keys(results.data[0]);
        setHeaders(keys);
        setYKeys([keys[1]]);
      },
    });
  };

  const exportChart = (id) => {
    const chart = document.getElementById(id);
    html2canvas(chart).then((canvas) => {
      const link = document.createElement("a");
      link.download = `${id}.png`;
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  const renderChart = () => {
    const keyX = headers[0];
    if (chartType === "Pie" && yKeys.length === 1) {
      return (
        <PieChart>
          <Tooltip />
          <Legend />
          <Pie
            data={data}
            dataKey={yKeys[0]}
            nameKey={keyX}
            cx="50%"
            cy="50%"
            outerRadius={100}
            label
          >
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
            ))}
          </Pie>
        </PieChart>
      );
    }

    const commonProps = {
      data,
      margin: { top: 20, right: 30, left: 20, bottom: 5 }
    };

    const renderYAxes = () =>
      yKeys.map((key, i) => (
        <YAxis
          key={`y-axis-${key}`}
          yAxisId={i}
          orientation={i % 2 === 0 ? "left" : "right"}
          stroke={colors[i % colors.length]}
        />
      ));

    const renderLines = () =>
      yKeys.map((key, i) => (
        <Line key={key} yAxisId={i} type="monotone" dataKey={key} stroke={colors[i % colors.length]} />
      ));

    const renderBars = () =>
      yKeys.map((key, i) => (
        <Bar key={key} yAxisId={i} dataKey={key} fill={colors[i % colors.length]} />
      ));

    const renderAreas = () =>
      yKeys.map((key, i) => (
        <Area key={key} yAxisId={i} type="monotone" dataKey={key} stroke={colors[i % colors.length]} fill={colors[i % colors.length]} />
      ));

    switch (chartType) {
      case "Bar":
        return (
          <BarChart {...commonProps}>
            <CartesianGrid stroke="#ccc" />
            <XAxis dataKey={keyX} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderBars()}
          </BarChart>
        );
      case "Area":
        return (
          <AreaChart {...commonProps}>
            <CartesianGrid stroke="#ccc" />
            <XAxis dataKey={keyX} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderAreas()}
          </AreaChart>
        );
      case "Scatter":
        return (
          <ScatterChart {...commonProps}>
            <CartesianGrid />
            <XAxis dataKey={keyX} name={keyX} />
            {renderYAxes()}
            <Tooltip cursor={{ strokeDasharray: "3 3" }} />
            <Legend />
            {yKeys.map((key, i) => (
              <Scatter key={key} yAxisId={i} name={key} data={data} fill={colors[i % colors.length]} dataKey={key} />
            ))}
          </ScatterChart>
        );
      case "Line":
      default:
        return (
          <LineChart {...commonProps}>
            <CartesianGrid stroke="#ccc" />
            <XAxis dataKey={keyX} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderLines()}
          </LineChart>
        );
    }
  };

  return (
    <div className="p-4 max-w-4xl mx-auto dark:bg-gray-900 dark:text-white min-h-screen">
      <h1 className="text-3xl font-bold mb-4">CSV Plotter</h1>

      <input type="file" accept=".csv" onChange={handleFileUpload} className="mb-4" />

      {data.length > 0 && headers.length >= 2 && (
        <>
          <div className="mb-4">
            <label className="mr-2 font-semibold">Select Chart Type:</label>
            <select
              value={chartType}
              onChange={(e) => setChartType(e.target.value)}
              className="border dark:bg-gray-800 dark:border-gray-600 px-2 py-1 rounded"
            >
              {chartTypes.map((type) => (
                <option key={type} value={type}>
                  {type} Chart
                </option>
              ))}
            </select>
          </div>

          <div className="mb-4">
            <label className="mr-2 font-semibold">Select Y-Axis Columns:</label>
            <select
              multiple
              value={yKeys}
              onChange={(e) => setYKeys([...e.target.selectedOptions].map((o) => o.value))}
              className="border dark:bg-gray-800 dark:border-gray-600 px-2 py-1 rounded w-full"
            >
              {headers.slice(1).map((header) => (
                <option key={header} value={header}>
                  {header}
                </option>
              ))}
            </select>
            <p className="text-sm text-gray-500 dark:text-gray-400">Hold Ctrl or Cmd to select multiple columns.</p>
          </div>

          <div id="chartContainer" className="bg-white dark:bg-gray-800 p-4 shadow rounded mb-4">
            <h2 className="text-xl font-semibold mb-2">{chartType} Chart</h2>
            <ResponsiveContainer width="100%" height={300}>
              {renderChart()}
            </ResponsiveContainer>
            <button
              onClick={() => exportChart("chartContainer")}
              className="mt-2 px-4 py-1 bg-blue-600 text-white rounded"
            >
              Download Chart
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
