import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import {
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area, ScatterChart, Scatter, PieChart, Pie, Cell
} from "recharts";
import html2canvas from "html2canvas";

const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#8dd1e1'];

function App() {
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [chartType, setChartType] = useState("line");
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [zoomDomain, setZoomDomain] = useState({ start: 0, end: 10 });

  // Toggle dark mode class on <html>
  useEffect(() => {
    const html = document.documentElement;
    if (darkMode) {
      html.classList.add("dark");
    } else {
      html.classList.remove("dark");
    }
  }, [darkMode]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const cleanedData = results.data.map(row => ({
          ...row,
          [Object.keys(row)[0]]: row[Object.keys(row)[0]].trim()
        }));
        setData(cleanedData);
        setHeaders(Object.keys(results.data[0]));
        setSelectedColumns([Object.keys(results.data[0])[1]]);
        setZoomDomain({ start: 0, end: results.data.length - 1 });
      },
    });
  };

  const handleChartTypeChange = (e) => setChartType(e.target.value);

  const handleColumnToggle = (col) => {
    setSelectedColumns(prev =>
      prev.includes(col) ? prev.filter(c => c !== col) : [...prev, col]
    );
  };

  const handleZoomIn = () => {
    setZoomDomain(prev => {
      const range = prev.end - prev.start;
      if (range <= 2) return prev;
      const increment = Math.ceil(range / 4);
      return {
        start: prev.start + increment,
        end: prev.end - increment
      };
    });
  };

  const handleZoomOut = () => {
    setZoomDomain(prev => {
      const increment = Math.ceil((prev.end - prev.start) / 2);
      return {
        start: Math.max(0, prev.start - increment),
        end: Math.min(data.length - 1, prev.end + increment)
      };
    });
  };

  const handleResetZoom = () => {
    setZoomDomain({ start: 0, end: data.length - 1 });
  };

  const renderYAxes = () => selectedColumns.map((col, i) => (
    <YAxis
      key={col}
      yAxisId={col}
      orientation={i % 2 === 0 ? 'left' : 'right'}
      stroke={COLORS[i % COLORS.length]}
    />
  ));

  const renderLines = () => selectedColumns.map((col, i) => (
    <Line key={col} yAxisId={col} type="monotone" dataKey={col} stroke={COLORS[i % COLORS.length]} />
  ));

  const renderBars = () => selectedColumns.map((col, i) => (
    <Bar key={col} yAxisId={col} dataKey={col} fill={COLORS[i % COLORS.length]} />
  ));

  const renderAreas = () => selectedColumns.map((col, i) => (
    <Area key={col} yAxisId={col} dataKey={col} fill={COLORS[i % COLORS.length]} stroke={COLORS[i % COLORS.length]} />
  ));

  const renderScatters = () => selectedColumns.map((col, i) => (
    <Scatter key={col} yAxisId={col} dataKey={col} fill={COLORS[i % COLORS.length]} />
  ));

  const renderChart = () => {
    switch(chartType) {
      case "line":
        return (
          <LineChart data={data.slice(zoomDomain.start, zoomDomain.end + 1)}>
            <CartesianGrid stroke="#ccc" />
            <XAxis
              dataKey={headers[0]}
              tickFormatter={value => value || "窶能

map.sourcesContent:
category"
            />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderLines()}
          </LineChart>
        );
      case "bar":
        return (
          <BarChart data={data.slice(zoomDomain.start, zoomDomain.end + 1)}>
            <CartesianGrid stroke="#ccc" />
            <XAxis
              dataKey={headers[0]}
              tickFormatter={value => value || "窶能

map:
category"
            />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderBars()}
          </BarChart>
        );
      case "area":
        return (
          <AreaChart data={data.slice(zoomDomain.start, zoomDomain.end + 1)}>
            <CartesianGrid stroke="#ccc" />
            <XAxis
              dataKey={headers[0]}
              tickFormatter={value => value || "窶能

category"
            />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderAreas()}
          </AreaChart>
        );
      case "scatter":
        return (
          <ScatterChart>
            <CartesianGrid stroke="#ccc" />
            <XAxis
              dataKey={headers[0]}
              tickFormatter={value => value || "窶能

category"
            />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderScatters()}
          </ScatterChart>
        );
      case "pie":
        const pieData = selectedColumns.map((col, i) => ({
          name: col,
          value: Number(data[0][col]) || 0,
          color: COLORS[i % COLORS.length],
        }));
        return (
          <PieChart>
            <Pie
              data={pieData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={80}
              fill="#8884d8"
              label
            >
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        );
      default: return null;
    }
  };

  return (
    <div className="p-4 max-w-4xl mx-auto min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-500">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">CSV Plotter</h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>

      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        className="mb-6 p-2 border rounded dark:bg-gray-800 dark:border-gray-600"
      />

      {data.length > 0 && (
        <>
          <select
            value={chartType}
            onChange={handleChartTypeChange}
            className="mb-4 p-2 border rounded dark:bg-gray-800 dark:border-gray-600"
          >
            <option value="line">Line Chart</option>
            <option value="bar">Bar Chart</option>
            <option value="area">Area Chart</option>
            <option value="scatter">Scatter Chart</option>
            <option value="pie">Pie Chart</option>
          </select>

          <div className="mb-4">
            <p className="mb-1 font-semibold">Select Columns to Plot:</p>
            {headers.slice(1).map((col) => (
              <label key={col} className="mr-4">
                <input
                  type="checkbox"
                  checked={selectedColumns.includes(col)}
                  onChange={() => handleColumnToggle(col)}
                  className="mr-1"
                />
                {col}
              </label>
            ))}
          </div>

          <div className="mb-4 flex gap-2">
            <button onClick={handleZoomIn} className="px-3 py-1 bg-blue-500 text-white rounded">Zoom In</button>
            <button onClick={handleZoomOut} className="px-3 py-1 bg-blue-500 text-white rounded">Zoom Out</button>
            <button onClick={handleResetZoom} className="px-3 py-1 bg-gray-600 text-white rounded">Reset</button>
          </div>

          <div>
            <button
              onClick={() => {
                const chartElement = document.getElementById("chart");
                html2canvas(chartElement).then(canvas => {
                  const link = document.createElement("a");
                  link.download = "chart.png";
                  link.href = canvas.toDataURL("image/png");
                  link.click();
                });
              }}
              className="mb-4 px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
            >
              Export as PNG
            </button>

            <div id="chart" className="bg-white dark:bg-gray-800 p-4 rounded shadow">
              <div className="overflow-x-auto">
                <div style={{ width: "1000px", height: "500px" }} className="bg-white dark:bg-gray-800 p-4 rounded shadow">
                  <ResponsiveContainer width="100%" height="100%">
                    {renderChart()}
                  </ResponsiveContainer>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
