import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import {
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  AreaChart, Area, ScatterChart, Scatter, PieChart, Pie, Cell
} from "recharts";
import html2canvas from "html2canvas";

const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#8dd1e1'];

function App() {
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [chartType, setChartType] = useState("line");
  const [selectedColumns, setSelectedColumns] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [zoomDomain, setZoomDomain] = useState({ start: 0, end: 10 });

  useEffect(() => {
    const html = document.documentElement;
    darkMode ? html.classList.add("dark") : html.classList.remove("dark");
  }, [darkMode]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        // Trim all string values in every row & every column
        const cleanedData = results.data.map(row => {
          const trimmedRow = {};
          Object.entries(row).forEach(([key, val]) => {
            trimmedRow[key] = typeof val === "string" ? val.trim() : val;
          });
          return trimmedRow;
        });
        setData(cleanedData);

        if (results.data.length > 0) {
          const cols = Object.keys(results.data[0]);
          setHeaders(cols);

          // Select the second column by default if exists, else empty
          setSelectedColumns(cols.length > 1 ? [cols[1]] : []);

          setZoomDomain({ start: 0, end: cleanedData.length - 1 });
        }
      },
      error: (err) => {
        console.error("Error parsing CSV:", err);
      }
    });
  };

  const handleChartTypeChange = (e) => setChartType(e.target.value);

  const handleColumnToggle = (col) => {
    setSelectedColumns(prev =>
      prev.includes(col) ? prev.filter(c => c !== col) : [...prev, col]
    );
  };

  // Zoom In: Shrink range by removing 1/4th from both ends
  const handleZoomIn = () => {
    setZoomDomain(prev => {
      const range = prev.end - prev.start;
      if (range <= 2) return prev; // Prevent zooming in too far
      const increment = Math.ceil(range / 4);
      const newStart = prev.start + increment;
      const newEnd = prev.end - increment;
      if (newStart >= newEnd) return prev; // Prevent invalid range
      return {
        start: newStart,
        end: newEnd,
      };
    });
  };

  // Zoom Out: Expand range by adding half of current range to ends
  const handleZoomOut = () => {
    setZoomDomain(prev => {
      const range = prev.end - prev.start;
      const increment = Math.ceil(range / 2);
      const newStart = Math.max(0, prev.start - increment);
      const newEnd = Math.min(data.length - 1, prev.end + increment);
      return {
        start: newStart,
        end: newEnd,
      };
    });
  };

  const handleResetZoom = () => {
    setZoomDomain({ start: 0, end: data.length - 1 });
  };

  // Render Y axes for all selected columns, alternating left/right
  const renderYAxes = () => selectedColumns.map((col, i) => (
    <YAxis
      key={col}
      yAxisId={col}
      orientation={i % 2 === 0 ? 'left' : 'right'}
      stroke={COLORS[i % COLORS.length]}
      domain={['auto', 'auto']}
      allowDataOverflow={true}
      // allowDecimals is true by default; can be tweaked if needed
    />
  ));

  const renderLines = () => selectedColumns.map((col, i) => (
    <Line
      key={col}
      yAxisId={col}
      type="monotone"
      dataKey={col}
      stroke={COLORS[i % COLORS.length]}
      dot={false}
      isAnimationActive={false}
    />
  ));

  const renderBars = () => selectedColumns.map((col, i) => (
    <Bar
      key={col}
      yAxisId={col}
      dataKey={col}
      fill={COLORS[i % COLORS.length]}
      isAnimationActive={false}
    />
  ));

  const renderAreas = () => selectedColumns.map((col, i) => (
    <Area
      key={col}
      yAxisId={col}
      dataKey={col}
      fill={COLORS[i % COLORS.length]}
      stroke={COLORS[i % COLORS.length]}
      isAnimationActive={false}
    />
  ));

  const renderScatters = () => selectedColumns.map((col, i) => (
    <Scatter
      key={col}
      yAxisId={col}
      dataKey={col}
      fill={COLORS[i % COLORS.length]}
      isAnimationActive={false}
    />
  ));

  // Render the chart, supporting normal (zoomed) and full modes
  const renderChart = (mode = "normal") => {
    if (!data.length || selectedColumns.length === 0 || headers.length === 0) {
      return <p className="text-center text-gray-500">Please upload CSV and select columns.</p>;
    }

    // Slice data based on zoom or show full data
    const chartData = mode === "full" ? data : data.slice(zoomDomain.start, zoomDomain.end + 1);

    const xAxisProps = {
      dataKey: headers[0],
      tickFormatter: (value) => value || "â€”",
      allowDuplicatedCategory: false,
      interval: 0,
      type: "category",
      minTickGap: 15,
      // tick={{ fontSize: 12 }}
    };

    switch(chartType) {
      case "line":
        return (
          <LineChart data={chartData} margin={{ top: 10, right: 50, left: 0, bottom: 50 }}>
            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
            <XAxis {...xAxisProps} angle={-45} textAnchor="end" height={60} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderLines()}
          </LineChart>
        );

      case "bar":
        return (
          <BarChart data={chartData} margin={{ top: 10, right: 50, left: 0, bottom: 50 }}>
            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
            <XAxis {...xAxisProps} angle={-45} textAnchor="end" height={60} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderBars()}
          </BarChart>
        );

      case "area":
        return (
          <AreaChart data={chartData} margin={{ top: 10, right: 50, left: 0, bottom: 50 }}>
            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
            <XAxis {...xAxisProps} angle={-45} textAnchor="end" height={60} />
            {renderYAxes()}
            <Tooltip />
            <Legend />
            {renderAreas()}
          </AreaChart>
        );

      case "scatter":
        return (
          <ScatterChart margin={{ top: 10, right: 50, left: 0, bottom: 50 }}>
            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
            <XAxis {...xAxisProps} angle={-45} textAnchor="end" height={60} />
            {renderYAxes()}
            <Tooltip cursor={{ strokeDasharray: '3 3' }} />
            <Legend />
            {renderScatters()}
          </ScatterChart>
        );

      case "pie":
        // Pie chart does not support zoom and uses only first data row
        const pieData = selectedColumns.map((col, i) => ({
          name: col,
          value: Number(data[0]?.[col]) || 0,
          color: COLORS[i % COLORS.length],
        }));

        // Show warning if no meaningful data
        if (pieData.every(d => d.value === 0)) {
          return <p className="text-center text-gray-500">Selected columns contain no numeric data for Pie Chart.</p>;
        }

        return (
          <PieChart>
            <Pie
              data={pieData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={80}
              fill="#8884d8"
              label
            >
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        );

      default:
        return null;
    }
  };

  // Disable zoom buttons if zoom limits reached or no data
  const canZoomIn = zoomDomain.end - zoomDomain.start > 2;
  const canZoomOut = !(zoomDomain.start === 0 && zoomDomain.end === data.length - 1);

  return (
    <div className="p-4 max-w-4xl mx-auto min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-500">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">CSV Plotter</h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="px-4 py-2 rounded bg-gray-300 dark:bg-gray-700 text-gray-900 dark:text-gray-100"
          aria-label="Toggle dark mode"
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>

      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        className="mb-6 p-2 border rounded dark:bg-gray-800 dark:border-gray-600"
        aria-label="Upload CSV file"
      />

      {data.length > 0 && (
        <>
          <select
            value={chartType}
            onChange={handleChartTypeChange}
            className="mb-4 p-2 border rounded dark:bg-gray-800 dark:border-gray-600"
            aria-label="Select chart type"
          >
            <option value="line">Line Chart</option>
            <option value="bar">Bar Chart</option>
            <option value="area">Area Chart</option>
            <option value="scatter">Scatter Chart</option>
            <option value="pie">Pie Chart</option>
          </select>

          <div className="mb-4">
            <p className="mb-1 font-semibold">Select Columns to Plot:</p>
            {headers.slice(1).map((col) => (
              <label key={col} className="mr-4 cursor-pointer select-none">
                <input
                  type="checkbox"
                  checked={selectedColumns.includes(col)}
                  onChange={() => handleColumnToggle(col)}
                  className="mr-1"
                />
                {col}
              </label>
            ))}
            {selectedColumns.length === 0 && (
              <p className="text-sm text-red-600 mt-2">Please select at least one column to plot.</p>
            )}
          </div>

          <div className="mb-4 flex gap-2">
            <button
              onClick={handleZoomIn}
              disabled={!canZoomIn}
              className={`px-3 py-1 rounded text-white ${canZoomIn ? "bg-blue-500 hover:bg-blue-600" : "bg-gray-400 cursor-not-allowed"}`}
              aria-label="Zoom in"
            >
              Zoom In
            </button>
            <button
              onClick={handleZoomOut}
              disabled={!canZoomOut}
              className={`px-3 py-1 rounded text-white ${canZoomOut ? "bg-blue-500 hover:bg-blue-600" : "bg-gray-400 cursor-not-allowed"}`}
              aria-label="Zoom out"
            >
              Zoom Out
            </button>
            <button
              onClick={handleResetZoom}
              className="px-3 py-1 bg-gray-600 text-white rounded hover:bg-gray-700"
              aria-label="Reset zoom"
            >
              Reset
            </button>
          </div>

          <div className="mb-4 flex flex-wrap gap-2">
            <button
              onClick={() => {
                const chartElement = document.getElementById("chart");
                if (!chartElement) return;
                html2canvas(chartElement).then(canvas => {
                  const link = document.createElement("a");
                  link.download = "chart.png";
                  link.href = canvas.toDataURL("image/png");
                  link.click();
                });
              }}
              className="px-4 py-2 rounded bg-blue-600 text-white hover:bg-blue-700"
              aria-label="Export zoomed chart as PNG"
            >
              Export as PNG
            </button>

            <button
              onClick={() => {
                const fullChart = document.getElementById("full-chart");
                if (!fullChart) return;
                html2canvas(fullChart).then(canvas => {
                  const link = document.createElement("a");
                  link.download = "full_chart.png";
                  link.href = canvas.toDataURL("image/png");
                  link.click();
                });
              }}
              className="px-4 py-2 rounded bg-green-600 text-white hover:bg-green-700"
              aria-label="Export full chart as PNG"
            >
              Export Full Graph as PNG
            </button>
          </div>

          <div
            id="chart"
            className="bg-white dark:bg-gray-800 p-4 rounded shadow overflow-x-auto"
            style={{ minWidth: 300 }}
          >
            <div style={{ width: "100%", height: "500px" }}>
              <ResponsiveContainer width="100%" height="100%">
                {renderChart()}
              </ResponsiveContainer>
            </div>
          </div>

          {/* Hidden chart for full export */}
          <div
            id="full-chart"
            style={{ position: "absolute", top: "-9999px", left: "-9999px", width: 1600, height: 700 }}
          >
            <ResponsiveContainer width="100%" height="100%">
              {renderChart("full")}
            </ResponsiveContainer>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
